mapper.update:
update notepad.unfinish_class
set unfinish_class_name = #{unfinish_class_name}
where unfinish_class_id = #{unfinish_class_id};

mapper.select:
select unfinish_class_name
from unfinish_class,unfinish
where unfinish.unfinish_class_id = unfinish_class.unfinish_class_id and user_id = #{user_id};
